import React, { Component } from 'react';
import { Api, JsonRpc, RpcError } from 'eosjs';
import endpoint from '../../validation/endpoint';
import axios from 'axios';


class Navbar extends Component {

    constructor(props){
        super(props);
        this.state = {
            maxLimit:10,
            currLimit:0,
            blockContainer:[],
            errors:'',
            block:{
                id:'',
                timestamp:'',
                transactions:[]
            }
        }
        this.getLatestBlocks = this.getLatestBlocks.bind(this);
    }

    async getLatestBlocks(){
        const rpc = new JsonRpc(endpoint);
        await rpc.get_info().then(data=>{
            console.log(data);
            if(data.head_block_num){
                return rpc.get_block(data.head_block_num).then(block=>{
                    console.log(block);
                })
            }
            //return rpc.get_block()
        }).catch(err => {
            this.setState({errors: err})
        });
    }

  render() {

    // console.log(rpc.get_block());
    console.log(this.getLatestBlocks());
    return (

        <div>
            <div className="row">
                <div className="col-md-12 text-center">
                    <h3 className="display-4 mb-4">Block.one development test</h3>
                </div>
            </div>
        </div>
    )
  }
}



export default Navbar;
