import React, { Component } from 'react';

class BlockInfo extends Component{

    constructor(props){
        super(props);
        this.showBlockInfo = this.showBlockInfo.bind(this);
        this.state = {
            showBlockInfoContainer: false
        }
    }

    showBlockInfo(){
        this.setState({
            showBlockInfoContainer: !this.state.showBlockInfoContainer
        })
    }


    render(){

        const blockContainerFlag = this.state.showBlockInfoContainer;

        let blockContainerHTML;

        let styles = {backgroundColor: '#eff0f1', padding:'15px', margin:'15px', width:'60%', fontSize: '14px'};

        if(blockContainerFlag){
            blockContainerHTML = (
                <div style={styles}>
                    <div><span style={{fontWeight: 'bold'}}>Id:</span><span>{this.props.information.id}</span></div>
                    <div><span style={{fontWeight: 'bold'}}>Producer:</span><span>{this.props.information.producer}</span></div>
                    <div><span style={{fontWeight: 'bold'}}>Block_num:</span><span>{this.props.information.block_num}</span></div>
                    <div><span style={{fontWeight: 'bold'}}>Ref_block_prefix:</span><span>{this.props.information.ref_block_prefix}</span></div>   
                    <div><span style={{fontWeight: 'bold'}}>Timestamp:</span><span>{this.props.information.timestamp}</span></div>
                    <div><span style={{fontWeight: 'bold'}}>Schedule_version:</span><span>{this.props.information.schedule_version}</span></div> 
                    <div><span style={{fontWeight: 'bold'}}>Action_mroot:</span><span>{this.props.information.action_mroot}</span></div> 
                    <div><span style={{fontWeight: 'bold'}}>Producer_signature:</span><span>{this.props.information.producer_signature}</span></div>      
                    <div><span style={{fontWeight: 'bold'}}>Transactions:</span><span>{this.props.information.transactions.length}</span></div>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
                </div>
            )
        }
        console.log(this.props.information);

        return(
            <div className="container" onClick={this.showBlockInfo}>
                <div className="row">
                    <div className="col-sm-7">{this.props.information.id}</div>
                    <div className="col-sm-3">{this.props.information.timestamp}</div>
                    <div className="col-sm-2">{this.props.information.transactions.length}</div>
                </div>
                <div className="row">
                    {blockContainerHTML}
                </div>
            </div>
        );
    }


}
