import React, { Component } from 'react';
import { JsonRpc } from 'eosjs';
import endpoint from '../../validation/endpoint';
import BlockInfo from '../commons/BlockInfo';

class Landing extends Component {

  constructor(props){
    super(props);
    this.state = {
        maxLimit:10,
        currLimit:0,
        blockContainer:[],
        errors:'',
        block:{}
    }
    this.getLatestBlocks = this.getLatestBlocks.bind(this);
}

 getLatestBlocks(){
    const rpc = new JsonRpc(endpoint);
      setInterval(()=>{
        rpc.get_info().then(data=>{
          console.log('s',data.head_block_num);
          if(data.head_block_num){
            console.log(data);
            rpc.get_block(data.head_block_num).then(block_data=>{
              if(block_data){
                this.setState({blockContainer:[...this.state.blockContainer,block_data]})
              }
            }) 
          }
        }).catch(err => {
            this.setState({errors: err})
        });
      },1000)
}

componentDidMount(){
    this.getLatestBlocks();
}

  render() {

    const { blockContainer } = this.state;

    let blockHeadContainer;

    if(blockContainer){
        blockHeadContainer = blockContainer.map(block => (
          <BlockInfo key={block.block_num} id={block.id} timestamp={block.timestamp} transactions={block.transactions.length} />
        ))
    }

    return (
      <div className="container">
          <div className="row">
              <div className="col-md-12">
                <h3>Latest Blocks</h3>
                  {blockHeadContainer}
              </div>
          </div>
      </div>
    )
  }
}

export default Landing;
