import React, { Component } from 'react'
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import TextFieldGroup from '../commons/TextFieldGroup';
import TextAreaFieldGroup from '../commons/TextAreaFieldGroup';
import SelectListGroup from '../commons/SelectListGroup';
import InputGroup from '../commons/InputGroup';
import { createProfile, getCurrentProfile } from '../../actions/profileActions';

class EditProfile extends Component {

  constructor(props){
    super(props);
    this.state = {
        displaySocialInputs:false,
        handle: '',
        company: '',
        website: '',
        status: '',
        location: '',
        bio: '',
        skills: '',
        youtube: '',
        facebook: '',
        twitter: '',
        instagram: '',
        experience:[],
        errors:''
    }

    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

componentDidMount(){
    this.props.getCurrentProfile();
}


componentWillReceiveProps(nextProps){
    if(nextProps.errors){
        this.setState({errors: nextProps.errors});
    }

    console.log(this.props.profile);
}

onChange(e){
    this.setState({[e.target.name]: e.target.value});
}

onSubmit(e){
    e.preventDefault();

    console.log(this.state);

    const profileData = {
        handle: this.state.handle,
        company: this.state.company,
        website: this.state.website,
        status: this.state.status,
        location: this.state.location,
        bio: this.state.bio,
        skills: this.state.skills,
        youtube: this.state.youtube,
        facebook: this.state.facebook,
        twitter: this.state.twitter,
        instagram: this.state.instagram,
        experience:[]
    }

    this.props.createProfile(profileData, this.props.history);


}



  render() {

    const { errors, displaySocialInputs } = this.state;

    let socialInputDiv;

    if(displaySocialInputs){
        socialInputDiv = (
            <div>
                <InputGroup 
                name="twitter"
                placeholder="Twitter Profile"
                value={this.state.twitter}
                onChange={this.onChange}
                error={errors.twitter}
                icon="fab fa-twitter"
                />
                <InputGroup 
                name="facebook"
                placeholder="Facebook Profile"
                value={this.state.facebook}
                onChange={this.onChange}
                error={errors.facebook}
                icon="fab fa-facebook"
                />
                <InputGroup 
                name="youtube"
                placeholder="Youtube Profile"
                value={this.state.youtube}
                onChange={this.onChange}
                error={errors.youtube}
                icon="fab fa-youtube"
                />
                <InputGroup 
                name="instagram"
                placeholder="Instagram Profile"
                value={this.state.instagram}
                onChange={this.onChange}
                error={errors.instagram}
                icon="fab fa-instagram"
                />
            </div>
        );
    }


    const selectOptions = [
        {label: "Select Current Status", value: ""},
        {label: "Software Engineer", value: "SoftwareEngineer"},
        {label: "Senior Software Engineer", value: "SeniorSoftwareEngineer"},
        {label: "Lead Software Engineer", value: "LeadSoftwareEngineer"}
    ];

    return (
      <div className="create-profile">
        <div className="container">
            <div className="row">
                <div className="col-md-8 m-auto">
                    <h1 className="display-4 text-center">Edit Profile</h1>
                    <small className="d-block pb-3">* = Required fields</small>
                    <form onSubmit={this.onSubmit}>
                        <TextFieldGroup
                        name="handle"
                        value={this.state.handle}
                        placeholder="Profile Handle"
                        error={errors.handle}
                        onChange={this.onChange}
                        info="A unique handle for your profile URL. Your full name, company name, nickname, etc (This CAN'T be changed later)"
                        />
                        <SelectListGroup
                        name="status"
                        value={this.state.status}
                        options={selectOptions}
                        onChange={this.onChange}
                        error={errors.status}
                        info="Give us an idea of where you are at in your career"
                        />
                        <TextFieldGroup
                        name="company"
                        value={this.state.company}
                        placeholder="Company"
                        error={errors.company}
                        onChange={this.onChange}
                        info="Could be your own company or one you work for"
                        />
                        <TextFieldGroup
                        name="website"
                        value={this.state.website}
                        placeholder="Website"
                        error={errors.website}
                        onChange={this.onChange}
                        info="Could be your own or a company website"
                        />
                        <TextFieldGroup
                        name="location"
                        value={this.state.location}
                        placeholder="Location"
                        error={errors.location}
                        onChange={this.onChange}
                        info="City & state suggested (eg. Boston, MA)"
                        />
                        <TextFieldGroup
                        name="skills"
                        value={this.state.skills}
                        placeholder="Skills"
                        error={errors.skills}
                        onChange={this.onChange}
                        info="Please use comma separated values (eg. HTML,CSS,JavaScript,PHP)"
                        />
                        <TextAreaFieldGroup
                        name="bio"
                        value={this.state.bio}
                        placeholder="A Short bio of yourself"
                        error={errors.bio}
                        onChange={this.onChange}
                        info="Tell us a little about yourself"
                        />
                        <div className="mb-3">
                            <button 
                            type="button"
                            onClick={()=>{
                                this.setState(state => ({
                                    displaySocialInputs: !state.displaySocialInputs
                                }))
                            }}
                            className="btn btn-light"
                            >
                            Add Social Network Links
                            </button>
                        </div>
                        {socialInputDiv}
                        <input 
                        type="submit"
                        value="button"
                        className="btn btn-info btn-block mt-4"
                        />
                    </form>
                </div>
            </div>
        </div>
      </div>
    )
  }
}

EditProfile.propTypes = {
    getCurrentProfile: PropTypes.func.isRequired,
    createProfile: PropTypes.func.isRequired,
    profile: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    profile: state.profile,
    errors: state.errors
})


export default connect(mapStateToProps,{ createProfile, getCurrentProfile })(withRouter(EditProfile));
