import React, { Component } from 'react';
import { JsonRpc } from 'eosjs';
import endpoint from '../../validation/endpoint';
import BlockInfo from '../commons/BlockInfo';
import Spinner from '../commons/Spinner';
import { setInterval } from 'timers';

class Landing extends Component {

  constructor(props){
    super(props);
    this.state = {
        maxLimit:10,
        currLimit:0,
        blockContainer:[],
        errors:'',
        block:{},
        rpcConnection:''
    }
    this.getLatestBlocks = this.getLatestBlocks.bind(this);
    this.stopReceivingBlock = this.stopReceivingBlock.bind(this);
    this.intervalID = null;
}

componentWillMount(){
  this.state.rpcConnection = new JsonRpc(endpoint);
}

stopReceivingBlock(){
  clearInterval(this.intervalID);
}

  getLatestBlocks(){
    let rpc = this.state.rpcConnection;
    this.intervalID = setInterval(() => {
      console.log('a', this.state.currLimit);
      if(this.state.currLimit === this.state.maxLimit){
        this.stopReceivingBlock(this.intervalID)
      }
      rpc.get_info().then(data=>{
        if(data.head_block_num){
          if(!this.state.block[data.head_block_num]){
            this.state.block[data.head_block_num] = data.head_block_num;
            rpc.get_block(data.head_block_num).then(block_data=>{
              if(block_data){
                this.setState({blockContainer:[...this.state.blockContainer,block_data]})
              }
              this.state.currLimit++;
            })
          }
          }}).catch(err => {
              this.setState({errors: err})
          });
    },500)
}

async componentDidMount(){
   await this.getLatestBlocks();
}

  render() {

    const { blockContainer } = this.state;

    let blockHeadContainer;

    let spinnerContainer;

    console.log(this.state.block);

    if(blockContainer.length===10){
        blockHeadContainer = blockContainer.map(block => (
          <BlockInfo key={block.block_num} id={block.id} timestamp={block.timestamp} transactions={block.transactions.length} />
        ))
    }else{
      spinnerContainer = <Spinner />
    }

    return (
      <div className="container">
          <div className="row">
              <div className="col-md-12">
                <h3>Latest Blocks</h3>
                <hr/>
                {spinnerContainer}
              </div>

                  <div className="col-sm-7"><p className="lead font-weight-bold">BlockID</p></div>
                  <div className="col-sm-3"><p className="lead font-weight-bold">Timestamp</p></div>
                  <div className="col-sm-2"><p className="lead font-weight-bold">Transactions</p></div>
                  {blockHeadContainer}
          </div>
      </div>
    )
  }
}

export default Landing;
