import React, { Component } from 'react'
import { getCurrentProfile, deleteProfile } from '../../actions/profileActions';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Spinner from '../commons/Spinner';
import ProfileActions from './ProfileActions';

class Dashboard extends Component {

  componentDidMount(){
    this.props.getCurrentProfile();
    console.log(this.props);
  }

  onDeleteClick(e){
      this.props.deleteProfile();
  }

  render() {

    const { user } = this.props.auth;

    const { profile, loading } = this.props.profile;

    let dashboardContent;

    if(profile === null || loading){
      dashboardContent = <Spinner />
    }else{
      if(Object.keys(profile).length>0){
        dashboardContent = (
          <div>
              <p className="lead text-muted"> Welcome <Link to={`/profile/${profile.handle}`}>{user.name}</Link></p>
              <ProfileActions />
              <div className="mt-5">
                  <button className="btn btn-danger" onClick={this.onDeleteClick.bind(this)}>Delete Account</button>
              </div>
          </div>
        );
      }else{
        dashboardContent = (
          <div>
              <p className="lead text-muted">Welcome {user.name}</p>
              <p>You have not setup a profile, please add some info.</p>
              <Link to='/create-profile' className="btn btn-lg btn-info">Create Profile</Link>
          </div>
        );
      }
    }

    return (
      <div className="dashboard">
          <div className="container">
              <div className="row">
                  <div className="col-md-12">
                      {dashboardContent}
                  </div>
                </div>
            </div>
        </div>
    )
  }
}

Dashboard.propTypes= {
  getCurrentProfile: PropTypes.func.isRequired,
  deleteProfile: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired
}

const mapStateToProps = (state) => ({
  auth: state.auth,
  profile: state.profile
})

export default connect(mapStateToProps, { getCurrentProfile, deleteProfile })(Dashboard);
