import React, { Component } from 'react'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import TextFieldGroup from '../commons/TextFieldGroup';
import TextAreaFieldGroup from '../commons/TextAreaFieldGroup';
import SelectListGroup from '../commons/SelectListGroup';
import InputGroup from '../commons/InputGroup';

class CreateProfile extends Component {

  constructor(props){
    super(props);
    this.state = {
        displaySocialInputs:false,
        handle: '',
        company: '',
        website: '',
        status: '',
        location: '',
        bio: '',
        skills: '',
        youtube: '',
        facebook: '',
        twitter: '',
        instagram: '',
        experience:[],
        errors:''
    }

    this.onChange = this.onChange.bind(this);

  }

onChange(e){
    this.setState({[e.target.name]: e.target.value});
}

onSubmit(e){
    e.preventDefault();
    console.log('Submit');
}



  render() {

    const { errors, displaySocialInputs } = this.state;

    const selectOptions = [
        {label: "Select Current Status", value: ""},
        {label: "Software Engineer", value: "SoftwareEngineer"},
        {label: "Senior Software Engineer", value: "SeniorSoftwareEngineer"},
        {label: "Lead Software Engineer", value: "LeadSoftwareEngineer"}
    ];

    return (
      <div className="create-profile">
        <div className="container">
            <div className="row">
                <div className="col-md-8 m-auto">
                    <h1 className="display-4 text-center">Create Your Profile</h1>
                    <p className="lead text-center">
                        Lets complete your profile information 
                    </p>
                    <small className="d-block pb-3">* = Required fields</small>
                    <form onSubmit={this.onSubmit}>
                        <TextFieldGroup
                        name="handle"
                        value={this.state.handle}
                        placeholder="Profile Handle"
                        error={errors.handle}
                        onChange={this.onChange}
                        info="A unique handle for your profile URL. Your full name, company name, nickname, etc (This CAN'T be changed later)"
                        />
                        <SelectListGroup
                        name="status"
                        options={selectOptions}
                        onChange={this.state.status}
                        error={errors.status}
                        info="Give us an idea of where you are at in your career"
                        />
                        <TextFieldGroup
                        name="company"
                        value={this.state.company}
                        placeholder="Company"
                        error={errors.company}
                        onChange={this.onChange}
                        info="Could be your own company or one you work for"
                        />
                        <TextFieldGroup
                        name="website"
                        value={this.state.website}
                        placeholder="Website"
                        error={errors.website}
                        onChange={this.onChange}
                        info="Could be your own or a company website"
                        />
                        <TextFieldGroup
                        name="location"
                        value={this.state.location}
                        placeholder="Location"
                        error={errors.location}
                        onChange={this.onChange}
                        info="City & state suggested (eg. Boston, MA)"
                        />
                        <TextFieldGroup
                        name="skills"
                        value={this.state.skills}
                        placeholder="Skills"
                        error={errors.skills}
                        onChange={this.onChange}
                        info="Please use comma separated values (eg. HTML,CSS,JavaScript,PHP)"
                        />
                        <TextAreaFieldGroup
                        name="bio"
                        value={this.state.bio}
                        placeholder="A Short bio of yourself"
                        error={errors.bio}
                        onChange={this.onChange}
                        info="Tell us a little about yourself"
                        />
                        <div className="mb-3">
                            <button onClick={()=>{
                                console.log('button-clicked');
                            }}>
                            Add Social Network Links
                            </button>
                        </div>

                    </form>
                </div>
            </div>
        </div>
      </div>
    )
  }
}

CreateProfile.propTypes = {
    profile: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    profile: state.profile,
    errors: state.errors
})


export default connect(mapStateToProps)(CreateProfile);
