import React, { Component } from 'react';
import { Api, JsonRpc, RpcError } from 'eosjs';
import endpoint from '../../validation/endpoint';
import axios from 'axios';
import Block from '../../interface/block';


class Navbar extends Component {

    constructor(props){
        super(props);
        this.state = {
            maxLimit:10,
            currLimit:0,
            blockContainer:[]
        }
    }

  render() {
      console.log(Block);
    const rpc = new JsonRpc(endpoint);
    (async () => { 
        await rpc.get_info().then(data=>{
            console.log(data);
            if(data.head_block_num){
                return rpc.get_block(data.head_block_num).then(block=>{
                    console.log(block);
                })
            }
            //return rpc.get_block()
        }); 
      })();
    // console.log(rpc.get_block());

    const title = (
        <h1>Hello world</h1>
    );

    return (
        <div>
            <div className="row">
                <div className="col-md-12 text-center">
                    <h3 className="display-4 mb-4">Block.one development test</h3>
                </div>
            </div>
        </div>
    )
  }
}



export default Navbar;
