import React, { Component } from 'react';
import { Api, JsonRpc, RpcError } from 'eosjs';
import endpoint from '../../validation/endpoint';
import BlockInfo from '../commons/BlockInfo';


class Navbar extends Component {

    constructor(props){
        super(props);
        this.state = {
            maxLimit:10,
            currLimit:0,
            blockContainer:[],
            errors:'',
            block:{}
        }
        this.getLatestBlocks = this.getLatestBlocks.bind(this);
    }

     getLatestBlocks(){
        const rpc = new JsonRpc(endpoint);
        rpc.get_info().then(data=>{

            if(data.head_block_num){
                return rpc.get_block(data.head_block_num).then(block_data=>{
                    if(block_data){
                        this.setState({block: block_data});
                        this.setState({blockContainer:[...this.state.blockContainer,block_data]})
                    }
                })
            }
        }).catch(err => {
            this.setState({errors: err})
        });
    }

    componentDidMount(){
        this.getLatestBlocks();
    }

  render() {
    //console.log(rpc.get_block());
    const { blockContainer } = this.state;

    let blockHeadContainer;

    if(blockContainer){
        blockHeadContainer = (
            <div>
                hello world
            </div>
        )
    }

    return (
        <div className="container">
            <div className="row">
                <div className="col-md-12">
                    {blockHeadContainer}
                </div>
            </div>
        </div>
    )
  }
}



export default Navbar;
